import { EXAMPLES, fetchExample } from './examples';
import { useStoredValue } from '../../../lib/localstorage';
import { useResource } from '../../../lib/use-resource';
import { parseStackTrace } from './errors';
import style from './style.module.css';
import REPL_CSS from './examples/style.css?raw';

/**
 * @param {Object} props
 * @param {string} props.code
 * @param {string} [props.slug]
 */
export function Repl({ code }) {
	const { route } = useLocation();
	const { query } = useRoute();
	const [editorCode, setEditorCode] = useStoredValue('preact-www-repl-code', code, true);
	const [runnerCode, setRunnerCode] = useState(editorCode);
	const [error, setError] = useState(null);
	const [copied, setCopied] = useState(false);

	// TODO: Needs some work for prerendering to not cause pop-in
	if (typeof window === 'undefined') return null;

	/**
	 * @type {{ Runner: import('./runner').default, CodeEditor: import('../../code-editor').default }}
	 */
	const { Runner, CodeEditor } = useResource(() => Promise.all([
		import('../../code-editor'),
		import('./runner')
	]).then(([CodeEditor, Runner]) => ({ CodeEditor: CodeEditor.default, Runner: Runner.default })), ['repl']);

	const applyExample = (e) => {
		const slug = e.target.value;
		fetchExample(slug)
			.then(code => {
				setEditorCode(code);
				setRunnerCode(code);
				route(`/repl?example=${encodeURIComponent(slug)}`, true);
		});
	};

	const onEditorInput = (code) => {
		setEditorCode(code);

		// Clears the (now outdated) example & code query params
		// when a user begins to modify the code
		if (location.search) {
			route('/repl', true);
		}
	};

	useEffect(() => {
		const delay = setTimeout(() => {
			setRunnerCode(editorCode);
		}, 250);
		return () => clearTimeout(delay);
	}, [editorCode]);

	const share = () => {
		// No reason to share semi-sketchy btoa'd code if there's
		// a perfectly good example we can use instead
		if (!query.example) {
			// We use `history.replaceState` here as the code is only relevant on mount.
			// There's no need to notify the router of the change.
			history.replaceState(null, null, `/repl?code=${encodeURIComponent(textToBase64(editorCode))}`);
		}

		try {
			let input = document.createElement('input');
			input.style.cssText = 'position:absolute; left:0; top:-999px;';
			input.value = location.href;
			document.body.appendChild(input);
			input.select();
			document.execCommand('copy');
			input.blur();
			document.body.removeChild(input);
			setCopied(true);
			setTimeout(() => setCopied(false), 2500);
		} catch (err) {
			// eslint-disable-next-line no-console
			console.log(err);
		}
	};

	const onRealm = realm => {
		realm.globalThis.githubStars = window.githubStars;
	};

	return (
		<>
			<header class={style.toolbar}>
				<label>
					Examples:{' '}
					<select value={query.example || ''} onChange={applyExample}>
						<option value="" disabled>
							Select Example...
						</option>
						{EXAMPLES.map(function item(ex) {
							const selected =
								ex.slug !== undefined && ex.slug === query.example;
							return ex.group ? (
								<optgroup label={ex.group}>{ex.items.map(item)}</optgroup>
							) : (
								<option selected={selected} value={ex.slug}>
									{ex.name}
								</option>
							);
						})}
					</select>
				</label>
				<button class={style.share} onClick={share}>
					{copied ? '🔗 Copied' : 'Share'}
				</button>
			</header>
			<div class={style.replWrapper}>
				<Splitter
					orientation="horizontal"
					other={
						<div class={style.output}>
							{error && (
								<ErrorOverlay
									name={error.name}
									message={error.message}
									stack={parseStackTrace(error)}
								/>
							)}
							<Runner
								onRealm={onRealm}
								onError={setError}
								onSuccess={() => setError(null)}
								css={REPL_CSS}
								code={runnerCode}
							/>
						</div>
					}
				>
					<CodeEditor
						class={style.code}
						value={editorCode}
						error={error}
						slug={query.example}
						onInput={onEditorInput}
					/>
				</Splitter>
			</div>
		</>
	);
}

/**
 * Encode text to base64 with unicode support
 *
 * @param {string} text
 */
export function textToBase64(text) {
	const bytes = new TextEncoder().encode(text);
	return btoa(String.fromCharCode(...bytes));
}

/**
 * Decode text from base64 with unicode support
 *
 * @param {string} base64
 */
export function base64ToText(base64) {
	const bytes = Uint8Array.from(atob(base64), c => c.charCodeAt(0));
	return new TextDecoder().decode(bytes);
}

import * as preact from 'preact';
import * as hooks from 'preact/hooks';
import * as debug from 'preact/debug';
import * as compat from 'preact/compat';
import * as htm from 'htm';
import * as htmPreact from 'htm/preact';
import * as preactCustomElement from 'preact-custom-element';
import * as preactSignals from '@preact/signals';
import * as preactSignalsCore from '@preact/signals-core';

let modules = {};
let moduleCache = {
	preact,
	'preact/hooks': hooks,
	'preact/debug': debug,
	'preact/compat': compat,
	react: compat,
	'react-dom': compat,
	htm,
	'@preact/signals': preactSignals,
	'@preact/signals-core': preactSignalsCore,
	'htm/preact': htmPreact,
	'preact-custom-element': preactCustomElement
};

window._require = function(id) {
	// flatten unpkg
	if (typeof id === 'string') {
		id = id.replace(/(^(https?:)?\/\/unpkg\.com\/|\?module$)/gi, '');
	}
	if (id in moduleCache) {
		return moduleCache[id];
	}
	if (id in modules) {
		return (moduleCache[id] = modules[id]());
	}
	throw Error(`No module found for ${id}`);
};

import '@babel/polyfill';
import { rollup } from '@rollup/browser';
import { transform } from 'sucrase';
import * as Comlink from 'comlink';
import { parseStackTrace } from './errors';

const PREPEND = `(function(module,exports){`;

// These are used by the Tutorial to inject solution detection.
const IMPORTS = `import * as $preact from 'preact';
import * as $hooks from 'preact/hooks';
Object.assign(self, {$preact:$preact,$hooks:$hooks},$preact);
self._require = m => self['$'+m];
`;

export function ping() {
	return true;
}

function transpile(code) {
	let codeUrl = `data:text/javascript;base64,${btoa(
		unescape(encodeURIComponent(code))
	)}`;
	try {
		return transform(code, {
			// prettier-ignore
			filePath: codeUrl,
			sourceMapOptions: {
				compiledFilename: 'repl.js'
			},
			transforms: ['jsx', 'typescript'],
			// omit _jsxSource junk
			production: true,
			jsxPragma: 'h',
			jsxFragmentPragma: 'Fragment'
		});
	} catch (err) {
		if (err.name !== 'SyntaxError' && /unexpected\stoken/i.test(err.message)) {
			let old = err;
			// eslint-disable-next-line no-ex-assign
			err = new SyntaxError(old.message);
			err.stack = old.stack;
			err.loc = old.loc;
		}

		if (err.name === 'SyntaxError') {
			err.message = err.message.replace(codeUrl, 'repl.js');
			const fileName = err.message.match(/([A-Za-z0-9_-]*\.js)/);
			// const fileName = 'repl.js';
			const loc = err.message.match(/(\d+):(\d+)\)/) || [0, 0, 0];
			err.message = err.message
				.replace(/Error\stransforming\s.*\.js:/, '')
				.replace(/\(.*\)$/, '');
			const stack = parseStackTrace(err);

			let line = loc[1];
			let column = loc[2];

			// sucrase's line mappings are not correct even with this fix.
			// It seems like don't generate correct mappings
			if (line > 0) {
				line = /\sexpected/.test(err.message) ? line - 2 : line;
			}

			stack.splice(1, 0, `    at ${fileName[1]} (:${line}:${column})\n`);
			err.stack = stack.join('\n');

			if (!err.loc) {
				err.loc = {
					line,
					column
				};
			}
		}
		throw err;
	}
}

async function bundle(sources) {
	const isSource = Object.prototype.hasOwnProperty.bind(sources);
	const bundle = await rollup({
		input: Object.keys(sources),
		treeshake: 'recommended',
		plugins: [
			{
				name: 'repl',
				resolveId(id, importer) {
					// Handle local source files
					if (isSource(id)) return id;

					// Resolve absolute path ids relative to their importer. For example,
					// esm.sh will return this internally. `https://esm.sh/preact` returns
					// `exports * from "/stable/preact@10.18.0/es2022/preact.mjs"`
					if (id[0] === '/') {
						if (!importer.match(/^https?:/)) {
							throw new Error(`Cannot resolve ${id} from ${importer}`);
						}

						try {
							return new URL(id, importer).href;
						} catch (e) {}
					}

					// If id is already an esm.sh url, add `?external=*` to it and return
					if (id.includes('://esm.sh/')) {
						const url = new URL(id);
						url.searchParams.set('external', '*');
						return url.href;
					}

					// Leave initial import, relative imports, & other http imports alone
					if (!importer || /(^\.\/|:\/\/)/.test(id)) {
						return id;
					}

					// For everything else (i.e. bare module specifiers), resolve to a
					// package on esm.sh. We'll support any syntax & options that esm.sh
					// supports
					const url = new URL(id, 'https://esm.sh/');
					url.searchParams.set('external', '*');
					return url.href;
				},
				async load(id) {
					if (isSource(id)) {
						const code = sources[id];
						const out = transpile(code);
						return out;
					}
					return get(id);
				}
			}
		]
	});
	const result = await bundle.generate({ format: 'cjs' });
	await bundle.close();
	return result.output[0];
}

// helper: cached http get text
const cache = new Map();
function get(url) {
	url = new URL(url).href;
	if (cache.has(url)) return cache.get(url);
	const p = fetch(url).then(r => (cache.set(r.url, p), r.text()));
	cache.set(url, p);
	return p;
}

export async function process(code, setup) {
	code = `${IMPORTS}${setup || ''}${code}`;

	const out = await bundle({
		'repl.js': code
	});

	// wrap & append sourceMap
	let transpiled = `${PREPEND}\n${(out && out.code) || ''}\n})`;

	if (transpiled && out.map) {
		try {
			// Use string concatentation here else `source-map-loader` will think it,
			// even with expression in the literal, is a legitimate source map identifier
			// and will try to load it.
			transpiled += '\n//# sourceMappingURL=' + out.map.toUrl();
		} catch (e) {
			console.error(`Source Map generation failed: ${e}`);
		}
	}

	return transpiled;
}

Comlink.expose({ ping, process });

import { Component, createRef } from 'preact';
import { memoize } from 'decko';
import style from './style.module.css';
import * as Comlink from 'comlink';
import { patchErrorLocation } from './errors';

let cachedFetcher = memoize(fetch);
let cachedFetch = (...args) => cachedFetcher(...args).then(r => r.clone());

const worker = Comlink.wrap(
	new Worker(new URL('./repl.worker.js', import.meta.url), {
		type: 'module'
	})
);

function createRoot(doc) {
	const root = doc.createElement('div');
	root.id = 'app';
	doc.body.appendChild(root);
}

export default class Runner extends Component {
	static worker = worker;

	frame = createRef();

	userUpdated = false;

	shouldComponentUpdate() {
		return false;
	}

	/** The parent DOM element into which this runner is rendering results. */
	get output() {
		return this.realm.globalThis.document.body;
	}

	commitError = error => {
		if (error && typeof error === 'object') {
			patchErrorLocation(error);
		}
		this.didError = true;
		if (this.props.onError) {
			this.props.onError(error);
		}
	};

	commitResult = result => {
		this.didError = false;
		if (this.props.onSuccess) {
			this.props.onSuccess(result);
		}
	};

	componentDidMount() {
		// Set up the realm synchronously so it's available when accessed via a ref.
		// To do that, we need to reuse then realm on the first run.
		this.setupRealm();
		this.realm.reuse = true;
		this.run();
	}

	componentWillReceiveProps({ code, setup }) {
		if (code !== this.props.code || setup !== this.props.setup) {
			this.userUpdated = true;
			this.run();
		}
	}

	run() {
		let { code, setup } = this.props;
		// onRealm must be called after imports but before user code:
		const fullSetup = `if (self._onRealm) self._onRealm();${setup || ''}\n`;
		this.running = worker
			.process(code, fullSetup)
			.then(transpiled => this.execute(transpiled))
			.then(this.commitResult)
			.catch(this.commitError)
			.then(() => {
				this.running = null;
				if (this.props.code !== code || this.props.setup !== setup) {
					this.run();
				}
			});
	}

	async rebuild() {
		await new Promise((resolve, reject) => {
			let frame = this.frame.current;
			frame.onload = resolve;
			frame.onerror = reject;
			frame.src = 'about:blank';
		});
		return await this.setup(true);
	}

	setup(fresh) {
		if (this.settingUp && fresh !== true) return this.settingUp;
		this.setupRealm();
		// silly leftover promise stuff
		return (this.settingUp = Promise.resolve());
	}

	setupRealm() {
		if (this.realm) {
			if (this.realm.reuse) {
				this.realm.reuse = false;
				return;
			}
			this.realm.destroy();
		}

		this.realm = new Realm({
			frame: this.frame.current,
			onError: this.commitError
		});
		this.realm.globalThis.fetch = cachedFetch;

		const insertStyles = () => {
			const doc = this.realm.globalThis.document,
				style = doc.createElement('style');
			style.appendChild(
				doc.createTextNode(`
					html { font: 100%/1.3 system-ui, sans-serif; background: none; }
					${this.props.css || ''}
				`)
			);
			doc.head.appendChild(style);
			createRoot(doc);
		};

		this.frame.current.contentDocument.readyState !== 'complete'
			? this.frame.current.onload = insertStyles
			: insertStyles();
	}

	async execute(transpiled, isFallback) {
		// TODO: This is much better implemented as a dialog within the frame.
		// `confirm` unforutnately is a window-level modal, so when/if the
		// code extends down past the window edge, the user can't see it.
		//
		// Using `<dialog>` as a frame-level modal would let the user check the
		// code in full before running it, though browser support is maybe not
		// ideal yet.
		if (
			new URLSearchParams(location.search).get('code') &&
			document.referrer &&
			document.referrer.indexOf(location.origin) !== 0 &&
			!this.userUpdated
		) {
			// eslint-disable-next-line no-alert
			const confirmed = confirm('Are you sure you want to run the code contained in this link?');
			if (!confirmed) return;
		}

		if (this.didError && !isFallback) {
			await this.rebuild();
		} else {
			await this.setup();
		}

		const base = this.output;
		const render = this.realm?.globalThis?.$preact?.render;

		if (this.didError) {
			// no need to reset, this is a fresh frame
		} else if (render || this.props.clear === true || isFallback === true) {
			if (render) {
				try {
					this.root = render(null, base);
				} catch (e) {
					console.error('Failed to unmount previous code: ', e);
				}
			}
			base.innerHTML = '';
			createRoot(this.realm.globalThis.document);
		}

		this.didError = undefined;

		let module = { exports: {} };

		// inject onRealm so it can be called after imports but before user code:
		this.realm.globalThis._onRealm = () => {
			this.realm.globalThis._onRealm = null;
			if (this.props.onRealm) {
				this.props.onRealm(this.realm);
			}
		};

		let fn = await this.realm.eval(transpiled);

		try {
			fn(module, module.exports);
		} catch (error) {
			// try once more without DOM reuse:
			if (isFallback !== true) {
				return await this.execute(transpiled, true);
			}
			throw error;
		}
	}

	render(props) {
		return (
			<iframe
				class={style.runner + ' ' + (props.class || '')}
				style={props.style}
				ref={this.frame}
				title="REPL Result"
			/>
		);
	}
}

function Realm({ frame, onError }) {
	if (!frame) {
		frame = document.createElement('iframe');
		frame.style.cssText =
			'position:absolute; left:0; top:-999em; width:1px; height:1px; overflow:hidden; border:0;';
		document.body.appendChild(frame);
	}
	this.reuse = false;
	this.globalThis = frame.contentWindow;
	this.onError = onError || console.error;

	const catchError = (m, fileName, lineNumber, columnNumber, err) => {
		if (err) return this.onError(err);
		let e = new Error(m);
		// lineNumber -= 5;
		let stack = `${m}\n  repl.js (:${lineNumber}:${columnNumber})`;
		Object.defineProperty(e, 'fileName', { value: fileName });
		Object.defineProperty(e, 'lineNumber', { value: lineNumber });
		Object.defineProperty(e, 'columnNumber', { value: columnNumber });
		Object.defineProperty(e, 'stack', { value: stack });
		this.onError(e);
	};
	const catchRejection = e => {
		this.onError(e.reason || e);
	};
	this.globalThis.onerror = catchError;
	this.globalThis.addEventListener('unhandledrejection', catchRejection);

	this.destroy = () => {
		this.globalThis.removeEventListener('unhandledrejection', catchRejection);
		this.globalThis.onerror = null;
		this.globalThis = this.onError = frame = onError = null;
	};
	this.eval = code => frame.contentWindow.eval(code);
}

import { useLocation, useRoute } from 'preact-iso';
import { Repl } from './repl';
import { base64ToText } from './repl/query-encode.js';
import { fetchExample } from './repl/examples';
import { useResource } from '../../lib/use-resource';
import { useContent } from '../../lib/use-content';

import style from './repl/style.module.css';

export default function ReplPage() {
	const { query } = useRoute();

	useContent('repl');

	const code = useResource(() => getInitialCode(query), [query]);

	return (
		<div class={style.repl}>
			<style>{`
				main {
					height: 100% !important;
					overflow: hidden !important;
				}
			`}</style>
			<Repl code={code} />
		</div>
	);
}

/**
 * Go down the list of fallbacks to get initial code
 *
 * ?code -> ?example -> localStorage -> simple counter example
 */
async function getInitialCode(query) {
	const { route } = useLocation();
	let code;
	if (query.code)  {
		code = base64ToText(query.code);
	} else if (query.example) {
		code = await fetchExample(query.example);
		if (!code) {
			route('/repl', true);
		}
	}

	if (!code) {
		if (typeof window !== 'undefined' && localStorage.getItem('preact-www-repl-code')) {
			code = localStorage.getItem('preact-www-repl-code');
		} else {
			const slug = 'counter-hooks';
			if (typeof window !== 'undefined') {
				route(`/repl?example=${encodeURIComponent(slug)}`, true);
			}
			code = await fetchExample(slug);
		}
	}

	return code;
}

import { options } from 'preact';
import {
	useState,
	useReducer,
	useEffect,
	useContext,
	useRef,
	useMemo,
	useCallback
} from 'preact/hooks';
import { useLocation } from 'preact-iso';
import { TutorialContext, SolutionContext } from './contexts';
import { ErrorOverlay } from '../repl/error-overlay';
import { parseStackTrace } from '../repl/errors';
import cx from '../../../lib/cx';
import { useResource } from '../../../lib/use-resource';
import { useLanguage } from '../../../lib/i18n';
import { Splitter } from '../../splitter';
import config from '../../../config.json';
import { MarkdownRegion } from '../markdown-region';
import style from './style.module.css';

const resultHandlers = new Set();
const realmHandlers = new Set();
const errorHandlers = new Set();

let resultCleanups, realmCleanups;

/**
 * @typedef TutorialCode
 * @property {string} setup
 * @property {string} initial
 * @property {string} final
 */

/**
 * @typedef TutorialMeta
 * @property {boolean} [code]
 * @property {boolean} [solvable]
 * @property {TutorialCode} [tutorial]
 */

/**
 * @param {{ html: string, meta: TutorialMeta }} props
 */
export function Tutorial({ html, meta }) {
	const { route, url } = useLocation();
	const [editorCode, setEditorCode] = useState(meta.tutorial?.initial || '');
	const [runnerCode, setRunnerCode] = useState(editorCode);
	const [error, setError] = useState(null);
	const [showCodeOverride, toggleCode] = useReducer(s => !s, true);

	const content = useRef(null);
	const runner = useRef(null);

	const solutionCtx = useContext(SolutionContext);

	const hasCode = meta.code !== false;
	const showCode = showCodeOverride && hasCode;

	// TODO: Needs some work for prerendering to not cause pop-in
	if (typeof window === 'undefined') return null;

	/**
	 * @type {{ Runner: import('../repl/runner').default, CodeEditor: import('../../code-editor').default }}
	 */
	const { Runner, CodeEditor } = useResource(() => Promise.all([
		import('../../code-editor'),
		import('../repl/runner')
	]).then(([CodeEditor, Runner]) => ({ CodeEditor: CodeEditor.default, Runner: Runner.default })), ['repl']);

	useEffect(() => {
		if (meta.tutorial?.initial && editorCode !== meta.tutorial.initial) {
			setEditorCode(meta.tutorial.initial);
			setRunnerCode(meta.tutorial.initial);
			solutionCtx.setSolved(false);
			content.current.scrollTo(0, 0);
		}
	}, [meta.tutorial?.initial]);

	useEffect(() => {
		const delay = setTimeout(() => {
			setRunnerCode(editorCode);
		}, 250);
		return () => clearTimeout(delay);
	}, [editorCode]);


	const useResult = fn => {
		useEffect(() => {
			resultHandlers.add(fn);
			return () => resultHandlers.delete(fn);
		}, [fn]);
	};
	const useRealm = fn => {
		useEffect(() => {
			realmHandlers.add(fn);
			let r = runner.current;
			if (r && r.realm && r.realm.globalThis._require) {
				onRealm(r.realm);
			}
			return () => realmHandlers.delete(fn);
		}, [fn]);
	};
	const useError = fn => {
		useEffect(() => {
			errorHandlers.add(fn);
			return () => errorHandlers.delete(fn);
		}, [fn]);
	};

	const onError = error => {
		errorHandlers.forEach(f => f(error));
		setError(error);
	};

	const onSuccess = () => {
		if (resultCleanups) resultCleanups.forEach(f => f());
		resultCleanups = [];
		resultHandlers.forEach(f => {
			let cleanup = f(runner.current);
			if (cleanup) resultCleanups.push(cleanup);
		});
		setError(null);
	};

	const onRealm = realm => {
		if (realmCleanups) realmCleanups.forEach(f => f());
		realmCleanups = [];
		// this.realmCleanups = Array.from(this.realmHandlers).map(f => f()).filter(Boolean);
		realmHandlers.forEach(f => {
			let cleanup = f(realm);
			if (cleanup) realmCleanups.push(cleanup);
		});
	};

	const help = () => {
		if (meta.tutorial?.final) {
			route(`${url}?solved`, true);
			setEditorCode(meta.tutorial?.final);
		}
	};

	return (
		<TutorialContext.Provider value={this}>
			<div
				class={cx(
					style.tutorialWrapper,
					meta.solvable && style.solvable,
					solutionCtx.solved && style.solved,
					showCode && style.showCode
				)}
			>
				<Splitter
					orientation="horizontal"
					force={!showCode ? '100%' : undefined}
					other={
						<Splitter
							orientation="vertical"
							other={
								<>
									<div class={style.output}>
										{error && (
											<ErrorOverlay
												name={error.name}
												message={error.message}
												stack={parseStackTrace(error)}
											/>
										)}
										<Runner
											ref={runner}
											onSuccess={onSuccess}
											onRealm={onRealm}
											onError={onError}
											code={runnerCode}
										/>
									</div>
									{hasCode && (
										<button
											class={style.toggleCode}
											title="Toggle Code"
											onClick={toggleCode}
										>
											<span>Toggle Code</span>
										</button>
									)}
								</>
							}
						>
							<div class={style.codeWindow}>
								<CodeEditor
									class={style.code}
									value={editorCode}
									error={error}
									slug={url}
									onInput={setEditorCode}
								/>
							</div>
						</Splitter>
					}
				>
					<div class={style.tutorialWindow} ref={content}>
						<MarkdownRegion
							html={html}
							meta={meta}
							components={TUTORIAL_COMPONENTS}
						/>

						{meta.tutorial?.setup &&
							<TutorialSetupBlock
								code={meta.tutorial.setup}
								runner={runner}
								useResult={useResult}
								useRealm={useRealm}
								useError={useError}
							/>
						}

						<ButtonContainer meta={meta} showCode={showCode} help={help} />
					</div>
				</Splitter>
			</div>
		</TutorialContext.Provider>
	);
}

function ButtonContainer({ meta, showCode, help }) {
	const [lang] = useLanguage();

	return (
		<div class={style.buttonContainer}>
			{meta.prev && (
				<a class={style.prevButton} href={meta.prev}>
					{config.i18n.previous[lang] || config.i18n.previous.en}
				</a>
			)}
			{meta.solvable && (
				<button
					class={style.helpButton}
					onClick={help}
					disabled={!showCode}
					title="Show solution to this example"
				>
					{config.i18n.tutorial.solve[lang] ||
						config.i18n.tutorial.solve.en}
				</button>
			)}
			{meta.next && (
				<a class={style.nextButton} href={meta.next}>
					{meta.nextText || config.i18n.next[lang] || config.i18n.next.en}
				</a>
			)}
		</div>
