export function getRouteName(route, lang) {
	return typeof route.name === 'object'
		? route.name[lang] || route.name.en
		: route.name || route.title;
}

function pathMatchesRoute(path, route) {
	if (!route || !route.path) return false;
	if (path === route.path) return true;
	let segs = path.replace(/(^\/|\/$)/g, '').split('/');
	let psegs = route.path.replace(/(^\/|\/$)/g, '').split('/');
	let len = Math.max(psegs.length, segs.length);
	for (let i = 0; i < len; i++) {
		let p = psegs[i];
		let s = segs[i];
		if (!p || (p[0] !== ':' && s !== p)) return false;
		if (!s) return /[?*]$/g.test(p);
		if (/[*+]$/g.test(p)) return true;
	}
	return true;
}

// get a CSS-addressable identifier for a given route
const getRouteIdent = route =>
	(getRouteName(route, 'en') || route.url)
		.toLowerCase()
		.replace(/[^a-z0-9]/i, '');

import { lazy, ErrorBoundary } from 'preact-iso';
import style from './style.module.css';
import config from '../../config.json';

const DocSearch = lazy(() => import('@docsearch/react').then(m => m.DocSearch));

// Might be a problem with the Algolia data, but it seemingly
// appends `#app` to all URLs without a hash fragment.
//
// It also returns the full prod URL, which isn't ideal for dev/staging
const transformItems = (items) =>
	items.map(i => {
			const url = new URL(i.url);
			return Object.assign(i, { url: url.pathname + url.hash.replace(/#app$/, '') });
		}
	);

export default function Search() {
	return (
		<div class={style.search}>
			<ErrorBoundary>
				<DocSearch
					apiKey={config.docsearch.apiKey}
					indexName={config.docsearch.indexName}
					appId={config.docsearch.appId}
					transformItems={transformItems}
				/>
			</ErrorBoundary>
		</div>
	);
}

