let initialized = false,
	prerenderData = {
		preactVersion: '',
		preactReleaseURL: '',
		preactStargazers: 0
	};
export async function prerender() {
	const init = async () => {
		// DOMParser polyfill for `preact-markup`
		const { DOMParser } = await import('@xmldom/xmldom');
		globalThis.DOMParser = DOMParser;

		// fetch latest release data
		const { default: releaseLambda } = await import('./lambda/release.js');
		const { version, url } = await (await releaseLambda()).json();
		prerenderData.preactVersion = version;
		prerenderData.preactReleaseURL = url;

		// fetch latest stargazer count
		const { default: repoLambda } = await import('./lambda/repo.js');
		const { stargazers_count: stargazersCount } = await (await repoLambda()).json();
		prerenderData.preactStargazers = stargazersCount;

		initialized = true;
	};
	if (!initialized) await init();

	const res = await ssr(<App prerenderData={prerenderData} />);

	const elements = new Set([
		{ type: 'meta', props: { name: 'description', content: globalThis.description } },
		{ type: 'meta', props: { property: 'og:url', content: `https://preactjs.com${location.pathname}` } },
		{ type: 'meta', props: { property: 'og:title', content: globalThis.title } },
		{ type: 'meta', props: { property: 'og:description', content: globalThis.description } },
		location.pathname.includes('/v8/') && { type: 'meta', props: { name: 'robots', content: 'noindex' } },
		process.env.BRANCH && { type: 'script', children: `ga=window.ga||function(){(ga.q=ga.q||[]).push(arguments)};ga('set','dimension1','${process.env.BRANCH}');onerror=function(e,f,l,c){ga('send','event','exception',e,f+':'+l+':'+c)}` }
	].filter(Boolean));

	res.html += '<script async defer src="https://www.google-analytics.com/analytics.js"></script>';

	return {
		...res,
		data: prerenderData,
		head: {
			lang: 'en',
			title: globalThis.title,
			elements
		}
	};
}

/*
 * @param {Request} [req]
 * @param {unknown} [_context]
 */
export default async function releaseLambda(req, _context) {
	const repo = req?.url ? new URL(req.url).searchParams.get('repo') : 'preact';

	const { version, url } = await fetchRelease(`preactjs/${repo}`);

	return new Response(JSON.stringify({ version, url }), {
		status: 200,
		headers: {
			'Content-Type': 'application/json',
			'Cache-Control': 'public, max-age=3600, stale-if-error=86400'
		}
	});
}

function checkStatus(r) {
	if (!r.ok) {
		throw new Error(`${r.status}: Request failed for '${r.url}'`);
	}

	return r;
}

const semverReg = /^.*?(\d+)\.(\d+)\.(\d+)(.*)?$/g;

/**
 * Parse semver version
 * @param {string} version
 */
function parseVersion(version) {
	semverReg.lastIndex = 0;
	const m = semverReg.exec(version);
	if (m) {
		return [+m[1], +m[2], +m[3], m[4]].filter(x => x !== undefined);
	}

	return [0, 0, 0];
}

const fetchRelease = repo =>
	fetch(`https://api.github.com/repos/${repo}/releases`)
		.then(checkStatus)
		.then(r => r.json())
		.then(d => {
			const releases = (d || []).sort((releaseA, releaseB) => {
				const a = parseVersion(releaseA.tag_name);
				const b = parseVersion(releaseB.tag_name);

				// Major
				if (a[0] > b[0]) return -1;
				if (a[0] < b[0]) return 1;

				// Minor
				if (a[1] > b[1]) return -1;
				if (a[1] < b[1]) return 1;

				// Patch
				if (a[2] && a.length === 3 > b[2]) return -1;
				if (a[2] && a.length === 3 < b[2]) return 1;

				// Pre-Releases
				if (a.length > 3 && b.length === 3) return 1;
				if (a.length === 3 && b.length > 3) return -1;
				return a[3] - b[3];
			});

			return {
				version: releases[0].tag_name,
				url: releases[0].html_url
			};
		});

/**
 * @param {Request} [req]
 * @param {unknown} [_context]
 */
export default async function repoLambda(req, _context) {
	const repo = req?.url
		? new URL(req.url).searchParams.get('repo')
		: 'preactjs/preact';

	const result = await repoInfo(repo);

	return new Response(JSON.stringify(result), {
		status: 200,
		headers: {
			'Content-Type': 'application/json',
			'Cache-Control': 'public, max-age=3600, stale-if-error=86400'
		}
	});
}

function checkStatus(r) {
	if (!r.ok) {
		throw new Error(`${r.status}: Request failed for '${r.url}'`);
	}

	return r;
}

const repoInfo = repo =>
	fetch(`https://api.github.com/repos/${repo}`)
		.then(checkStatus)
		.then(r => r.json());

/**
 * Fetch and parse a markdown document with optional JSON FrontMatter.
 * @returns {Promise<{ content: string, meta: {toc:{text:string, id:string, level:number}[], title: string}, html: string }>}
 */
export async function getContent([lang, name]) {
	let path = `/content/${lang}`,
		url = `${path}/${name.replace(/^\//, '')}`,
		ext = (url.match(/\.([a-z]+)$/i) || [])[1];
	if (!ext)
		url = url.endsWith('/') ? url.replace(/\/$/, '.json') : url + '.json';

	let fallback = false;
	return await fetch(url)
		.then(r => {
			// fall back to english
			if (!r.ok && lang != 'en') {
				fallback = true;
				return fetch(url.replace(/content\/[^/]+\//, 'content/en/'));
			}
			return r;
		})
		.then(r => {
			if (r.ok) return r;
			return fetch(`${path}/${r.status}.json`);
		})
		.then(r => r.json())
		.then(data => {
			data.meta.isFallback = fallback;
			return data;
		});
}

