import { useEffect, useState } from 'preact/hooks';

/**
 * On mobile devices 100vh refers to the whole viewport **without** the
 * address bar. So when the address bar is visible we receive the wrong
 * height. We try to work around this by measuring the actual height
 * instead of relying on css units alone.
 */
export function useOverlayToggle() {
	const [open, setOpen] = useState(false);

	useEffect(() => {
		if (open) setHeight();

		function onResize() {
			if (open) {
				// If we open the mobile menu on mobile and resize to a
				// desktop breakpoint, we need to clear the "open" state
				if (window.innerWidth >= convertRemToPixels(50)) {
					setOpen(false);
				}
				setHeight();
			}
		}
		window.addEventListener('resize', onResize);
		return () => window.removeEventListener('resize', onResize);
	}, [open]);

	return /** @type {const} */ ([open, setOpen]);
}

function setHeight() {
	let vh = window.innerHeight;
	document.documentElement.style.setProperty('--vh', `${vh}px`);
}

function convertRemToPixels(rem) {
	return rem * Number(getComputedStyle(document.documentElement).fontSize);
}

import { useEffect } from 'preact/hooks';

import { createTitle } from './page-title';
import { fetchContent } from './use-resource.js';

/**
 * @param {string} path
 * @returns {{ html: string, meta: any }}
 */
export function useContent(path) {
	const { html, meta } = fetchContent(path);
	useTitle(meta.title);
	useDescription(meta.description);

	return { html, meta };
}

/**
 * Set `document.title`
 * @param {string} title
 */
export function useTitle(title) {
	if (typeof window === 'undefined') {
		globalThis.title = createTitle(title);
	}
	useEffect(() => {
		if (title) {
			document.title = createTitle(title);
		}
	}, [title]);
}

/**
 * Set the meta description tag content
 * @param {string} text
 */
export function useDescription(text) {
	if (typeof window === 'undefined') {
		globalThis.description = text;
	}
	useEffect(() => {
		const el = document.querySelector('meta[name=description]');
		if (text && el) {
			el.setAttribute('content', text);
		}
	}, [text]);
}

import { useEffect } from 'preact/hooks';

import { getContent } from './content.js';
import { useLanguage } from './i18n';

/**
 * @typedef {Object} CacheEntry
 * @property {Promise<any>} promise
 * @property {'pending'|'success'|'error'} status
 * @property {any} result
 * @property {number} users
 */

/** @type {Map<string, CacheEntry>} */
const CACHE = new Map();
const createCacheKey = (fn, deps) => '' + fn + JSON.stringify(deps);

/**
 * @param {string} path
 */
export function prefetchContent(path) {
	const lang = document.documentElement.lang;
	const cacheKey = createCacheKey(() => getContent([lang, path]), [lang, path]);
	if (CACHE.has(cacheKey)) return;

	setupCacheEntry(() => getContent([lang, path]), cacheKey);
}

/**
 * @param {string} path
 * @returns {{ html: string, meta: any }}
 */
export function fetchContent(path) {
	const [lang] = useLanguage();
	return useResource(() => getContent([lang, path]), [lang, path]);
}

export function useResource(fn, deps) {
	const cacheKey = createCacheKey(fn, deps);

	let state = CACHE.get(cacheKey);
	if (!state) {
		state = setupCacheEntry(fn, cacheKey);
	}

	useEffect(() => {
		state.users++;

		return () => {
			// Delete cached Promise if nobody uses it anymore
			if (state.users-- <= 0) {
				CACHE.delete(cacheKey);
			}
		};
	}, [cacheKey, state]);

	if (state.status === 'success') return state.result;
	else if (state.status === 'error') throw state.result;
	throw state.promise;
}

/**
 * @param {() => Promise<any>} fn
 * @param {string} cacheKey
 * @returns {CacheEntry}
 */
function setupCacheEntry(fn, cacheKey) {
	/** @type {CacheEntry} */
	const state = { promise: fn(), status: 'pending', result: undefined, users: 0 };

	if (state.promise.then) {
		state.promise
			.then(r => {
				state.status = 'success';
				state.result = r;
			})
			.catch(err => {
				state.status = 'error';
				state.result = err;
			});
	} else {
		state.status = 'success';
		state.result = state.promise;
	}

	CACHE.set(cacheKey, state);
	return state;
}

/// <reference types="vite/client" />

declare global {
