import { LocationProvider, ErrorBoundary } from 'preact-iso';
import { LanguageProvider } from '../lib/i18n';
import { PrerenderDataProvider } from '../lib/prerender-data.jsx';
import Header from './header';
import Routes from './routes';

/**
 * @param {{ prerenderData?: import('../types.d.ts').PrerenderData }} props
 */
export default function App({ prerenderData }) {
	return (
		<ErrorBoundary>
			<LocationProvider>
				<LanguageProvider>
					<PrerenderDataProvider value={prerenderData}>
						<Header />
						<Routes />
					</PrerenderDataProvider>
				</LanguageProvider>
			</LocationProvider>
		</ErrorBoundary>
	);
}

import { Time } from '../time';
import config from '../../config.json';
import style from './style.module.css';

export default function BlogMeta({ meta }) {
	return (
		<div class={style.blogMeta}>
			{meta.date && <Time value={meta.date} />}
			{Array.isArray(meta.authors) && meta.authors.length > 0 && (
				<>
					, written by{' '}
					<address class={style.authors}>
						{meta.authors.map((author, i, arr) => {
							const authorData = config.blogAuthors.find(
								data => data.name === author
							);
							return (
								<AuthorLinks
									authorData={authorData}
									author={author}
									i={i}
									arr={arr}
								/>
							);
						})}
						{(meta.translation_by || []).map((author, i, arr) => {
							const authorData = config.blogAuthors.find(
								data => data.name === author
							);
							return (
								<>
									{', translated by '}
									<AuthorLinks
										authorData={authorData}
										author={author}
										i={i}
										arr={arr}
									/>
								</>
							);
						})}
					</address>
				</>
			)}
		</div>
	);
}

function AuthorLinks({ authorData, author, i, arr }) {
	return (
		<span key={author}>
			{authorData ? (
				<a href={authorData.link} target="_blank" rel="noopener noreferrer">
					{author}
				</a>
			) : (
				<span>{author}</span>
			)}
			{i < arr.length - 2 ? ', ' : i === arr.length - 2 ? ' and ' : null}
		</span>
	);
}

import config from '../../config.json';
import { useLanguage, useTranslation } from '../../lib/i18n';
import { getRouteName } from '../header';
import { Time } from '../time';
import { prefetchContent } from '../../lib/use-resource';
import s from './style.module.css';

export default function BlogOverview() {
	const [lang] = useLanguage();
	const continueReading = useTranslation('continueReading');

	return (
		<div>
			<div class={s.postList}>
				{config.blog.map(post => {
					const name = getRouteName(post, lang);
					const excerpt = post.excerpt[lang] || post.excerpt.en;
					const onMouseOver = () => prefetchContent(post.path);

					return (
						<article class={s.post}>
							<div class={s.meta}>
								<Time value={post.date} />
							</div>
							<h2 class={s.title}>
								<a href={post.path} onMouseOver={onMouseOver}>{name}</a>
							</h2>
							<p class={s.excerpt}>{excerpt}</p>
							<a href={post.path} onMouseOver={onMouseOver} class="btn-small">
								{continueReading} &rarr;
							</a>
						</article>
					);
				})}
			</div>
		</div>
	);
}

import style from './style.module.css';
import config from '../../config.json';

export default function Branding() {
	return (
		<section class={style.logos}>
			{config.branding.map(asset => (
				<LogoVariation name={asset.name} alt={asset.alt} />
			))}
		</section>
	);
}

function LogoVariation({ name, alt }) {
	return (
		<div class={style.variation}>
			<img
				src={`/branding/${name}.svg`}
				alt={alt}
				loading="lazy"
				height="64"
			/>
			<div class={style.links}>
				<a
					href={`/branding/${name}.svg`}
					target="_blank"
					rel="noreferrer"
				>
					SVG
				</a>
				<a
					href={`/branding/${name}.png`}
					target="_blank"
					rel="noreferrer"
				>
					PNG
				</a>
			</div>
		</div>
	);
}

import { useRef, useEffect } from 'preact/hooks';
import { EditorView } from 'codemirror';
import { lineNumbers, keymap, highlightActiveLineGutter, highlightActiveLine } from '@codemirror/view';
import { EditorState, Transaction } from '@codemirror/state';
import { defaultKeymap, history, historyKeymap, indentWithTab } from '@codemirror/commands';
import { javascript } from '@codemirror/lang-javascript';
import { syntaxHighlighting, HighlightStyle, indentUnit, bracketMatching } from '@codemirror/language';
import { tags } from '@lezer/highlight';
import { closeBrackets, autocompletion } from '@codemirror/autocomplete';
import cx from '../../lib/cx';

import style from './style.module.css';
import './code-mirror.css';

// Custom theme that better matches our Prism config, though
// the lexer is somewhat limited so it still deviates
const highlightStyle = HighlightStyle.define([
	{ tag: tags.keyword, class: 'cm-keyword' },
	{ tag: [tags.definition(tags.function(tags.name)), tags.function(tags.name), tags.propertyName], class: 'cm-function' },
	{ tag: tags.literal, class: 'cm-literal' },
	{ tag: tags.tagName, class: 'cm-tag' },
	{ tag: tags.attributeName, class: 'cm-attribute' },
	{ tag: tags.string, class: 'cm-string' },
	{ tag: [tags.operator], class: 'cm-operator' },
	{ tag: tags.comment, class: 'cm-comment' },
	{ tag: tags.invalid, class: 'cm-invalid' }
]);

/**
 * @param {object} props
 * @param {string} props.value
 * @param {(value: string) => void} props.onInput
 * @param {any} props.error - Unused at this time
 * @param {string} props.slug
 * @param {string} [props.class]
 */
export default function CodeEditor(props) {
	const editorParent = useRef(null);
	/** @type {{ current: EditorView | null }} */
	const editor = useRef(null);

	const routeHasChanged = useRef(false);

	useEffect(() => {
		if (props.slug || !editor.current) routeHasChanged.current = true;
	}, [props.slug]);

	useEffect(() => {
		if (routeHasChanged.current === false) return;
		routeHasChanged.current = false;

		if (editor.current) {
			editor.current.dispatch({
				changes: { from: 0, to: editor.current.state.doc.length, insert: props.value }
			});
			return;
		}

		const theme = EditorView.theme({}, { dark: true });

		const state = EditorState.create({
			doc: props.value,
			extensions: [
				lineNumbers(),
				highlightActiveLine(),
				highlightActiveLineGutter(),
				history(),
				indentUnit.of('\t'),
				closeBrackets(),
				bracketMatching(),
				autocompletion(),
				javascript({ jsx: true }),
				keymap.of([indentWithTab, ...defaultKeymap, ...historyKeymap]),
				[theme, syntaxHighlighting(highlightStyle, { fallback: true })],
				EditorView.updateListener.of(update => {
					// Ignores changes from swapping out the editor code programmatically
					if (isViewUpdateFromUserInput(update)) {
						props.onInput(update.state.doc.toString());
					}
				})
			]
		});

		editor.current = new EditorView({
			state,
			parent: editorParent.current
		});
	}, [props.value]);

	useEffect(() => (
		() => {
			if (editor.current) editor.current.destroy();
		}
	), []);

	return <div ref={editorParent} class={cx(style.codeEditor, props.class)} />;
}

/** @param {import('@codemirror/view').ViewUpdate} viewUpdate */
function isViewUpdateFromUserInput(viewUpdate) {
	if (viewUpdate.docChanged) {
		for (const transaction of viewUpdate.transactions) {
			if (transaction.annotation(Transaction.userEvent)) return true;
		}
	}
	return false;
}

import { useEffect } from 'preact/hooks';
import Markup from 'preact-markup';
import widgets from '../widgets';
import style from './style.module.css';
import { useTranslation } from '../../lib/i18n';
import { TocContext } from '../table-of-contents';
import { prefetchContent } from '../../lib/use-resource';

const COMPONENTS = {
	...widgets,
	a(props) {
		if (props.href && props.href.startsWith('/')) {
			const url = new URL(props.href, location.origin);
			props.onMouseOver = () => prefetchContent(url.pathname);
		}

		return <a {...props} />;
	}
};

function SiblingNav({ route, lang, start }) {
	let title = '';
	let url = '';
	if (route) {
		url = route.path.toLowerCase();
		title =
			typeof route.name === 'object'
				? route.name[lang || 'en']
				: route.name || route.title;
	}
	const label = useTranslation(start ? 'previous' : 'next');

	return (
		<a class={style.nextLink} data-dir-end={!start} href={url}>
			{start && <span class={style.icon}>&larr;&nbsp;</span>}
			{!start && <span class={style.icon}>&nbsp;&rarr;</span>}
			<span class={style.nextInner}>
				<span class={style.nextTitle}>
					<span class={style.nextTitleInner}>{title}</span>
				</span>
				<span class={style.nextUrl}>{label}</span>
			</span>
		</a>
	);
}

export default function ContentRegion({ content, components, ...props }) {
	const hasNav = !!(props.next || props.prev);
	components = Object.assign({}, COMPONENTS, components);

	useEffect(() => {
		const hash = location.hash;
		if (hash) {
			// Hack to force a scroll
			location.hash = '';
			location.hash = hash;
		}
	}, [props.current]);

	return (
		<content-region name={props.current} data-page-nav={hasNav} can-edit={props.canEdit}>
			{content && (
				<TocContext.Provider value={{ toc: props.toc }}>
					<Markup
						markup={content}
						type="html"
						trim={false}
						components={components}
					/>
				</TocContext.Provider>
			)}
			{hasNav && (
				<div class={style.nextWrapper}>
					{props.prev ? (
						<SiblingNav start lang={props.lang} route={props.prev} />
					) : (
						<span />
					)}
					{props.next ? (
						<SiblingNav lang={props.lang} route={props.next} />
					) : (
						<span />
					)}
				</div>
			)}
		</content-region>
	);
}

import { useRoute } from 'preact-iso';
import { useContent } from '../../lib/use-content';
import { NotFound } from './not-found';
import { MarkdownRegion } from './markdown-region';
import Footer from '../footer/index';
import { blogRoutes } from '../../lib/route-utils';
import style from './style.module.css';

export default function BlogPage() {
	const { params } = useRoute();
	const { slug } = params;

	if (!blogRoutes[`/blog/${slug}`]) {
		return <NotFound />;
	}

	return <BlogLayout />;
}

function BlogLayout() {
	const { path } = useRoute();
	const { html, meta } = useContent(path);

	return (
		<div class={style.page}>
			<div class={style.outer}>
				<div class={style.inner}>
					<MarkdownRegion html={html} meta={meta} />
					<Footer />
				</div>
			</div>
		</div>
	);
}

import { useRoute } from 'preact-iso';
import { useContent } from '../../lib/use-content';
import config from '../../config.json';
import { NotFound } from './not-found';
import cx from '../../lib/cx';
import { MarkdownRegion } from './markdown-region';
import Sidebar from '../sidebar';
import Footer from '../footer/index';
import { docRoutes } from '../../lib/route-utils';
import { LATEST_MAJOR } from '../doc-version';
import style from './style.module.css';

export function DocPage() {
	const { params } = useRoute();
	const { version, name } = params;

	if (!docRoutes[version]['/' + name]) {
		return <NotFound />;
	}

	return <DocLayout isGuide />;
}

export function DocLayout({ isGuide = false }) {
	const { path } = useRoute();
	const { html, meta } = useContent(path === '/' ? 'index' : path);

	return (
		<div class={cx(style.page, isGuide && style.withSidebar)}>
			<div class={style.outer}>
				{isGuide && (
					<div class={style.sidebarWrap}>
						<Sidebar />
					</div>
				)}
				<div class={style.inner}>
					{isGuide && <OldDocsWarning />}
					<MarkdownRegion html={html} meta={meta} />
					<Footer />
				</div>
			</div>
		</div>
	);
}

function OldDocsWarning() {
	const { name, version } = useRoute().params;

	if (version === LATEST_MAJOR) {
		return null;
	}

	const latestExists = config.docs[LATEST_MAJOR].some(section =>
		section.routes.some(route => route.path === '/' + name)
	);

	return (
		<div class={style.oldDocsWarning}>
			You are viewing the documentation for an older version of Preact.
			{latestExists ? (
				<>
					{' '}
					Switch to the{' '}
					<a href={`/guide/${LATEST_MAJOR}/${name}`}>current version</a>.
				</>
			) : (
				<>
					{' '}
					Get started with the{' '}
					<a href={`/guide/${LATEST_MAJOR}/getting-started`}>current version</a>
					.
				</>
			)}
		</div>
	);
}

import { Fragment } from 'preact';
import { useRoute } from 'preact-iso';
import EditThisPage from '../edit-button';
import ContentRegion from '../content-region';
import BlogMeta from '../blog-meta';

/**
 * @param {object} props
 * @propery {string} html
 * @propery {any} meta
 * @propery {any} [components]
 */
export function MarkdownRegion({ html, meta, components }) {
	const { path } = useRoute();

	const canEdit = path !== '/' && !path.startsWith('/tutorial');
	const isBlogArticle = path.startsWith('/blog/');

	return (
		<Fragment key={path}>
			{canEdit && <EditThisPage isFallback={meta.isFallback} />}
			{isBlogArticle && <BlogMeta meta={meta} />}
			<ContentRegion
				current={path}
				content={html}
				toc={meta.toc}
				components={components}
				canEdit={canEdit}
			/>
		</Fragment>
	);
}

import { useContent } from '../../lib/use-content';
import Footer from '../footer';
import { MarkdownRegion } from './markdown-region';
import style from './style.module.css';

export function NotFound() {
	const { html, meta } = useContent('404');

	return (
		<div class={style.page}>
			<div class={style.outer}>
				<div class={style.inner}>
					<MarkdownRegion html={html} meta={meta} />
					<Footer />
				</div>
			</div>
		</div>
	);
}

import { useRoute } from 'preact-iso';
import { navRoutes } from '../../lib/route-utils';
import { NotFound } from './not-found';
import { DocLayout } from './doc-page';

export function Page() {
	const { path } = useRoute();

	if (!navRoutes[path]) {
		return <NotFound />;
	}

	return <DocLayout />;
}

import { useReducer } from 'preact/hooks';
import style from './error-overlay.module.css';

/**
 * @param {Object} props
 * @param {string} props.name
 * @param {string} props.message
 * @param {{functionName: string, line: number, column: number}[]} props.stack
 * @param {string} [props.class]
 */
export function ErrorOverlay({ name, message, stack, class: c }) {
	const [showStack, toggleStack] = useReducer(s => !s, false);
	const hasStack = stack && stack.length > 0;

	return (
		<div class={`${style.root} ${c || ''}`}>
			<h5>
				{name}: {message}
			</h5>
			{hasStack && (
				<button
					class={style.showStack + ' ' + (showStack ? style.showing : '')}
					onClick={toggleStack}
				>
					Error Stack
				</button>
			)}
			{hasStack && showStack && (
				<div class={style.stack}>
					{stack.map(frame => (
						<p class={style.frame}>
							{frame.functionName} :{frame.line}:{frame.column}
						</p>
					))}
				</div>
			)}
		</div>
	);
}

import { useState, useEffect } from 'preact/hooks';
import { useLocation, useRoute } from 'preact-iso';
import { Splitter } from '../../splitter';
import { textToBase64 } from './query-encode.js';
import { ErrorOverlay } from './error-overlay';
import { EXAMPLES, fetchExample } from './examples';
import { useStoredValue } from '../../../lib/localstorage';
import { useResource } from '../../../lib/use-resource';
import { parseStackTrace } from './errors';
import style from './style.module.css';
import REPL_CSS from './examples/style.css?raw';

/**
 * @param {Object} props
 * @param {string} props.code
 * @param {string} [props.slug]
 */
export function Repl({ code }) {
	const { route } = useLocation();
	const { query } = useRoute();
	const [editorCode, setEditorCode] = useStoredValue('preact-www-repl-code', code, true);
	const [runnerCode, setRunnerCode] = useState(editorCode);
	const [error, setError] = useState(null);
	const [copied, setCopied] = useState(false);

	// TODO: Needs some work for prerendering to not cause pop-in
	if (typeof window === 'undefined') return null;

	/**
	 * @type {{ Runner: import('./runner').default, CodeEditor: import('../../code-editor').default }}
	 */
	const { Runner, CodeEditor } = useResource(() => Promise.all([
		import('../../code-editor'),
		import('./runner')
	]).then(([CodeEditor, Runner]) => ({ CodeEditor: CodeEditor.default, Runner: Runner.default })), ['repl']);

	const applyExample = (e) => {
		const slug = e.target.value;
		fetchExample(slug)
			.then(code => {
				setEditorCode(code);
				setRunnerCode(code);
				route(`/repl?example=${encodeURIComponent(slug)}`, true);
		});
	};

	const onEditorInput = (code) => {
		setEditorCode(code);

		// Clears the (now outdated) example & code query params
		// when a user begins to modify the code
		if (location.search) {
			route('/repl', true);
		}
	};

	useEffect(() => {
		const delay = setTimeout(() => {
			setRunnerCode(editorCode);
		}, 250);
		return () => clearTimeout(delay);
	}, [editorCode]);

	const share = () => {
		// No reason to share semi-sketchy btoa'd code if there's
		// a perfectly good example we can use instead
		if (!query.example) {
			// We use `history.replaceState` here as the code is only relevant on mount.
			// There's no need to notify the router of the change.
			history.replaceState(null, null, `/repl?code=${encodeURIComponent(textToBase64(editorCode))}`);
		}

		try {
			let input = document.createElement('input');
			input.style.cssText = 'position:absolute; left:0; top:-999px;';
			input.value = location.href;
			document.body.appendChild(input);
			input.select();
			document.execCommand('copy');
			input.blur();
			document.body.removeChild(input);
			setCopied(true);
			setTimeout(() => setCopied(false), 2500);
		} catch (err) {
			// eslint-disable-next-line no-console
			console.log(err);
		}
	};

	const onRealm = realm => {
		realm.globalThis.githubStars = window.githubStars;
	};

	return (
		<>
			<header class={style.toolbar}>
				<label>
					Examples:{' '}
					<select value={query.example || ''} onChange={applyExample}>
						<option value="" disabled>
							Select Example...
						</option>
						{EXAMPLES.map(function item(ex) {
							const selected =
								ex.slug !== undefined && ex.slug === query.example;
							return ex.group ? (
								<optgroup label={ex.group}>{ex.items.map(item)}</optgroup>
							) : (
								<option selected={selected} value={ex.slug}>
									{ex.name}
								</option>
							);
						})}
					</select>
				</label>
				<button class={style.share} onClick={share}>
					{copied ? 'ðŸ”— Copied' : 'Share'}
				</button>
			</header>
			<div class={style.replWrapper}>
				<Splitter
					orientation="horizontal"
					other={
						<div class={style.output}>
							{error && (
								<ErrorOverlay
									name={error.name}
									message={error.message}
									stack={parseStackTrace(error)}
								/>
							)}
							<Runner
								onRealm={onRealm}
								onError={setError}
								onSuccess={() => setError(null)}
								css={REPL_CSS}
								code={runnerCode}
							/>
						</div>
					}
				>
					<CodeEditor
						class={style.code}
						value={editorCode}
						error={error}
						slug={query.example}
						onInput={onEditorInput}
					/>
				</Splitter>
			</div>
		</>
	);
}

import { Component, createRef } from 'preact';
import { memoize } from 'decko';
import style from './style.module.css';
import * as Comlink from 'comlink';
import { patchErrorLocation } from './errors';

let cachedFetcher = memoize(fetch);
let cachedFetch = (...args) => cachedFetcher(...args).then(r => r.clone());

const worker = Comlink.wrap(
	new Worker(new URL('./repl.worker.js', import.meta.url), {
		type: 'module'
	})
);

function createRoot(doc) {
	const root = doc.createElement('div');
	root.id = 'app';
	doc.body.appendChild(root);
}

export default class Runner extends Component {
	static worker = worker;

	frame = createRef();

	userUpdated = false;

	shouldComponentUpdate() {
		return false;
	}

	/** The parent DOM element into which this runner is rendering results. */
	get output() {
		return this.realm.globalThis.document.body;
	}

	commitError = error => {
		if (error && typeof error === 'object') {
			patchErrorLocation(error);
		}
		this.didError = true;
		if (this.props.onError) {
			this.props.onError(error);
		}
	};

	commitResult = result => {
		this.didError = false;
		if (this.props.onSuccess) {
			this.props.onSuccess(result);
		}
	};

	componentDidMount() {
		// Set up the realm synchronously so it's available when accessed via a ref.
		// To do that, we need to reuse then realm on the first run.
		this.setupRealm();
		this.realm.reuse = true;
		this.run();
	}

	componentWillReceiveProps({ code, setup }) {
		if (code !== this.props.code || setup !== this.props.setup) {
			this.userUpdated = true;
			this.run();
		}
	}

	run() {
		let { code, setup } = this.props;
		// onRealm must be called after imports but before user code:
		const fullSetup = `if (self._onRealm) self._onRealm();${setup || ''}\n`;
		this.running = worker
			.process(code, fullSetup)
			.then(transpiled => this.execute(transpiled))
			.then(this.commitResult)
			.catch(this.commitError)
			.then(() => {
				this.running = null;
				if (this.props.code !== code || this.props.setup !== setup) {
					this.run();
				}
			});
	}

	async rebuild() {
		await new Promise((resolve, reject) => {
			let frame = this.frame.current;
			frame.onload = resolve;
			frame.onerror = reject;
			frame.src = 'about:blank';
		});
		return await this.setup(true);
	}

	setup(fresh) {
		if (this.settingUp && fresh !== true) return this.settingUp;
		this.setupRealm();
		// silly leftover promise stuff
		return (this.settingUp = Promise.resolve());
	}

	setupRealm() {
		if (this.realm) {
			if (this.realm.reuse) {
				this.realm.reuse = false;
				return;
			}
			this.realm.destroy();
		}

		this.realm = new Realm({
			frame: this.frame.current,
			onError: this.commitError
		});
		this.realm.globalThis.fetch = cachedFetch;

		const insertStyles = () => {
			const doc = this.realm.globalThis.document,
				style = doc.createElement('style');
			style.appendChild(
				doc.createTextNode(`
					html { font: 100%/1.3 system-ui, sans-serif; background: none; }
					${this.props.css || ''}
				`)
			);
			doc.head.appendChild(style);
			createRoot(doc);
		};

		this.frame.current.contentDocument.readyState !== 'complete'
			? this.frame.current.onload = insertStyles
			: insertStyles();
	}

	async execute(transpiled, isFallback) {
		// TODO: This is much better implemented as a dialog within the frame.
		// `confirm` unforutnately is a window-level modal, so when/if the
		// code extends down past the window edge, the user can't see it.
		//
		// Using `<dialog>` as a frame-level modal would let the user check the
		// code in full before running it, though browser support is maybe not
		// ideal yet.
		if (
			new URLSearchParams(location.search).get('code') &&
			document.referrer &&
			document.referrer.indexOf(location.origin) !== 0 &&
			!this.userUpdated
		) {
			// eslint-disable-next-line no-alert
			const confirmed = confirm('Are you sure you want to run the code contained in this link?');
			if (!confirmed) return;
		}

		if (this.didError && !isFallback) {
			await this.rebuild();
		} else {
			await this.setup();
		}

		const base = this.output;
		const render = this.realm?.globalThis?.$preact?.render;

		if (this.didError) {
			// no need to reset, this is a fresh frame
		} else if (render || this.props.clear === true || isFallback === true) {
			if (render) {
				try {
					this.root = render(null, base);
				} catch (e) {
					console.error('Failed to unmount previous code: ', e);
				}
			}
			base.innerHTML = '';
			createRoot(this.realm.globalThis.document);
		}

		this.didError = undefined;

		let module = { exports: {} };

		// inject onRealm so it can be called after imports but before user code:
		this.realm.globalThis._onRealm = () => {
			this.realm.globalThis._onRealm = null;
			if (this.props.onRealm) {
				this.props.onRealm(this.realm);
			}
		};

		let fn = await this.realm.eval(transpiled);

		try {
			fn(module, module.exports);
		} catch (error) {
			// try once more without DOM reuse:
			if (isFallback !== true) {
				return await this.execute(transpiled, true);
			}
			throw error;
		}
	}

	render(props) {
		return (
			<iframe
				class={style.runner + ' ' + (props.class || '')}
				style={props.style}
				ref={this.frame}
				title="REPL Result"
			/>
		);
	}
}

function Realm({ frame, onError }) {
	if (!frame) {
		frame = document.createElement('iframe');
		frame.style.cssText =
			'position:absolute; left:0; top:-999em; width:1px; height:1px; overflow:hidden; border:0;';
		document.body.appendChild(frame);
	}
	this.reuse = false;
	this.globalThis = frame.contentWindow;
	this.onError = onError || console.error;

	const catchError = (m, fileName, lineNumber, columnNumber, err) => {
		if (err) return this.onError(err);
		let e = new Error(m);
		// lineNumber -= 5;
		let stack = `${m}\n  repl.js (:${lineNumber}:${columnNumber})`;
		Object.defineProperty(e, 'fileName', { value: fileName });
		Object.defineProperty(e, 'lineNumber', { value: lineNumber });
		Object.defineProperty(e, 'columnNumber', { value: columnNumber });
		Object.defineProperty(e, 'stack', { value: stack });
		this.onError(e);
	};
	const catchRejection = e => {
		this.onError(e.reason || e);
	};
	this.globalThis.onerror = catchError;
	this.globalThis.addEventListener('unhandledrejection', catchRejection);

	this.destroy = () => {
		this.globalThis.removeEventListener('unhandledrejection', catchRejection);
		this.globalThis.onerror = null;
		this.globalThis = this.onError = frame = onError = null;
	};
	this.eval = code => frame.contentWindow.eval(code);
}

import { useLocation, useRoute } from 'preact-iso';
import { Repl } from './repl';
import { base64ToText } from './repl/query-encode.js';
import { fetchExample } from './repl/examples';
import { useResource } from '../../lib/use-resource';
import { useContent } from '../../lib/use-content';

import style from './repl/style.module.css';

export default function ReplPage() {
	const { query } = useRoute();

	useContent('repl');

	const code = useResource(() => getInitialCode(query), [query]);

	return (
		<div class={style.repl}>
			<style>{`
				main {
					height: 100% !important;
					overflow: hidden !important;
				}
			`}</style>
			<Repl code={code} />
		</div>
	);
}

/**
 * Go down the list of fallbacks to get initial code
 *
 * ?code -> ?example -> localStorage -> simple counter example
 */
async function getInitialCode(query) {
	const { route } = useLocation();
	let code;
	if (query.code)  {
		code = base64ToText(query.code);
	} else if (query.example) {
		code = await fetchExample(query.example);
		if (!code) {
			route('/repl', true);
		}
	}

	if (!code) {
		if (typeof window !== 'undefined' && localStorage.getItem('preact-www-repl-code')) {
			code = localStorage.getItem('preact-www-repl-code');
		} else {
			const slug = 'counter-hooks';
			if (typeof window !== 'undefined') {
				route(`/repl?example=${encodeURIComponent(slug)}`, true);
			}
			code = await fetchExample(slug);
		}
	}

	return code;
}

import { createContext } from 'preact';
import { useState } from 'preact/hooks';

