// It also returns the full prod URL, which isn't ideal for dev/staging
const transformItems = (items) =>
	items.map(i => {
			const url = new URL(i.url);
			return Object.assign(i, { url: url.pathname + url.hash.replace(/#app$/, '') });
		}
	);

