import * as preact from 'preact';
import * as hooks from 'preact/hooks';
import { hydrate, prerender as ssr } from 'preact-iso';

import App from './components/app';
import './analytics';
import './style/index.css';

// allows users to play with preact in the browser developer console
globalThis.preact = { ...preact, ...hooks };


if (typeof window !== 'undefined') {
	hydrate(<App />, document.getElementById('app'));

	// Might need to keep this around indefinitely, unfortunately
	if ('serviceWorker' in navigator) {
		navigator.serviceWorker.getRegistrations().then(registrations => {
			for (const registration of registrations) {
				registration.unregister();
			}
		});
	}
}

let initialized = false,
	prerenderData = {
		preactVersion: '',
		preactReleaseURL: '',
		preactStargazers: 0
	};
export async function prerender() {
	const init = async () => {
		// DOMParser polyfill for `preact-markup`
		const { DOMParser } = await import('@xmldom/xmldom');
		globalThis.DOMParser = DOMParser;

		// fetch latest release data
		const { default: releaseLambda } = await import('./lambda/release.js');
		const { version, url } = await (await releaseLambda()).json();
		prerenderData.preactVersion = version;
		prerenderData.preactReleaseURL = url;

		// fetch latest stargazer count
		const { default: repoLambda } = await import('./lambda/repo.js');
		const { stargazers_count: stargazersCount } = await (await repoLambda()).json();
		prerenderData.preactStargazers = stargazersCount;

		initialized = true;
	};
	if (!initialized) await init();

	const res = await ssr(<App prerenderData={prerenderData} />);

	const elements = new Set([
		{ type: 'meta', props: { name: 'description', content: globalThis.description } },
		{ type: 'meta', props: { property: 'og:url', content: `https://preactjs.com${location.pathname}` } },
		{ type: 'meta', props: { property: 'og:title', content: globalThis.title } },
		{ type: 'meta', props: { property: 'og:description', content: globalThis.description } },
		location.pathname.includes('/v8/') && { type: 'meta', props: { name: 'robots', content: 'noindex' } },
		process.env.BRANCH && { type: 'script', children: `ga=window.ga||function(){(ga.q=ga.q||[]).push(arguments)};ga('set','dimension1','${process.env.BRANCH}');onerror=function(e,f,l,c){ga('send','event','exception',e,f+':'+l+':'+c)}` }
	].filter(Boolean));

	res.html += '<script async defer src="https://www.google-analytics.com/analytics.js"></script>';

	return {
		...res,
		data: prerenderData,
		head: {
			lang: 'en',
			title: globalThis.title,
			elements
		}
	};
}

import { createContext } from 'preact';
import { useContext, useEffect } from 'preact/hooks';
import { useLocation } from 'preact-iso';
import { useStoredValue } from './localstorage';
import config from '../config.json';

/**
 * @typedef LanguageContext
 * @property {string} lang
 * @property {(string) => void} setLang
 */

/**
 * @type {import('preact').Context<LanguageContext>}
 */
const LanguageContext = createContext(/** @type {LanguageContext} */ ({}));

/**
 * Get the default language based on the preferred preference of the browser
 * @param {Record<string, string>} available All available languages
 * @param {string} [override]
 * @returns {string | undefined}
 */
export function getDefaultLanguage(available, override) {
	if (typeof navigator === 'undefined') return;

	// Override via `?lang=foo` parameter
	if (override && config.languages[override]) {
		return override;
	}

	let langs = [navigator.language].concat(navigator.languages);
	for (let i = 0; i < langs.length; i++) {
		if (langs[i]) {
			let lang = String(langs[i]).toLowerCase();
			if (available[lang]) return lang;
			// Respect order of `navigator.languages` by returning if the fallback language `English` is found
			if (lang === 'en') return;
		}
	}
}

export function LanguageProvider({ children }) {
	const { query } = useLocation();

	const [lang, setLang] = useStoredValue(
		'lang',
		getDefaultLanguage(config.languages, query.lang) || 'en',
		!!query.lang
	);

	useEffect(() => {
		document.documentElement.lang = lang;
	}, []);

	const setAndUpdateHtmlAttr = (lang) => {
		setLang(lang);
		document.documentElement.lang = lang;
	};

	return (
		<LanguageContext.Provider value={{ lang, setLang: setAndUpdateHtmlAttr }}>
			{children}
		</LanguageContext.Provider>
	);
}

/**
 * Handles all logic related to language settings
 * @returns {[string, (v: string) => void]}
 */
export function useLanguage() {
	const { lang, setLang } = useContext(LanguageContext);
	return [lang, setLang];
}

/**
 * Get the translation of a key. Defaults to english if no translation is found
 * @param {string} key
 */
export function useTranslation(key) {
	const [lang] = useLanguage();
	const data = config.i18n[key];
	return data[lang] || data.en;
}

import { createContext } from 'preact';
import { useContext } from 'preact/hooks';

/**
 * @typedef {import('../types.d.ts').PrerenderData} PrerenderData
 */

/**
 * @returns {PrerenderData | {}}
 */
export function getFallbackData() {
	if (typeof window === 'undefined') return {};
	const el = document.getElementById('preact-prerender-data');
	if (!el) return {};
	const data = JSON.parse(el.textContent);
	return data;
}

/**
 * @type {import('preact').Context<PrerenderData>}
 */
const PrerenderDataContext = createContext(/** @type {PrerenderData} */ ({}));

/**
 * @param {{ value?: PrerenderData, children: any }} props
 */
export function PrerenderDataProvider({ value, children }) {
	const fallbackData = getFallbackData();

	const preactVersion = value?.preactVersion || fallbackData.preactVersion;
	const preactReleaseURL = value?.preactReleaseURL || fallbackData.preactReleaseUrl;
	const preactStargazers = value?.preactStargazers || fallbackData.preactStargazers;

	return (
		<PrerenderDataContext.Provider value={{ preactVersion, preactReleaseURL, preactStargazers }}>
			{children}
		</PrerenderDataContext.Provider>
	);
}

/**
 * @returns {PrerenderData}
 */
export function usePrerenderData() {
	return useContext(PrerenderDataContext);
}
