export const localStorageGet = key => {
	try {
		return localStorage.getItem(key);
	} catch (e) {
		return null;
	}
};

export const localStorageSet = (key, value) => {
	try {
		localStorage.setItem(key, value);
	} catch (e) {}
};

/**
 * Automatically sync a value to localStorage
 * @param {string} key The key to store the data in
 * @param {any} initial Initial value when no localStorage entry was found
 * @param {boolean} [force] Ignore stored value, use initial instead
 * @returns {[any, (v: any) => void]}
 */
export function useStoredValue(key, initial, force) {
	let stored = localStorageGet(key);
	if (force || stored == null) stored = initial;

	const [value, setValue] = useState(stored);

	function setStoredValue(v) {
		localStorageSet(key, v);
		setValue(v);
	}

	return [value, setStoredValue];
}

/**
 * @param {string} title
 */
export function createTitle(title) {
	const url = location.pathname;

	// Titles for various content areas
	let suffix = '';
	switch (true) {
		// Shouldn't be an issue, but `startsWith` is wildly faster than `includes`
		case url.startsWith('/guide/v10'):
			suffix = 'Preact Guide';
			break;
		case url.startsWith('/tutorial'):
			suffix = 'Preact Tutorial';
			break;
		case url.startsWith('/guide/v8'):
			suffix = 'Preact Version 8';
			break;
		default:
			suffix = 'Preact';
	}

	if (suffix !== title) title += ' ‚Äì ' + suffix;
	return title;
}

